import 'package:firebase_database/firebase_database.dart';import 'package:flutter/material.dart';import 'package:go_router/go_router.dart';import 'package:untitled1/router/app_route_name.dart';/// Screen1 ekranıclass Screen1 extends StatefulWidget {  /// Screen1 ekranı  const Screen1({super.key});  @override  State<Screen1> createState() => _Screen1State();}class _Screen1State extends State<Screen1> {  @override  void initState() {    // TODO: implement initState    super.initState();    artir();  }  int _counter = 0;  Future<void> artir() async {     await FirebaseDatabase.instance.ref('counter').runTransaction((transaction) {       final data = transaction as int? ?? 0;       _counter = data + 1;       return Transaction.success(_counter);     }).then((value) => setState(() {}));  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          Padding(            padding: const EdgeInsets.all(8.0),            child: Text('Sayaç : $_counter'),          ),          Center(            child: ElevatedButton(              onPressed: artir,              child: const Text('Arttır'),            ),          ),        ],      ),    );  }}